namespace Depso;

public class ServiceProviderAttributeInfo : AttributeInfo
{
	public ServiceProviderAttributeInfo() : base("ServiceProvider")
	{
	}

	protected override string GetSourceCode()
	{
		return $$"""
			// <auto-generated/>
			
			#nullable enable

			namespace {{Constants.GeneratorNamespace}}
			{
			    [global::System.AttributeUsage(global::System.AttributeTargets.Class)]
			    internal sealed class {{NameWithAttribute}} : global::System.Attribute
			    {
			    }
			}
			""".Trim();
	}
}